mapper.select:
		select `t_optometry_id`,`student_id`,`checkor_id`,`first_check_vd`,`first_check_rps`,`first_check_lps`,`first_check_rcs`,`first_check_lcs`,`check_date`,date_format(s_result_optometry.check_date, '%Y-%m-%d') as checkDate1,`second_check_vd`,`second_check_rps`,`second_check_lps`,`second_check_rcs`,`second_check_lcs`,`delete_flag`,first_check_pd ,second_check_pd,`identity_card`
		from s_result_optometry

		  		   and t_optometry_id = #{tOptometryId} 
		  		   and student_id = #{studentId} 
		  		   and checkor_id = #{checkorId} 
		  		   and first_check_vd = #{firstCheckVd} 
		  		   and first_check_rps = #{firstCheckRps} 
		  		   and first_check_lps = #{firstCheckLps} 
		  		   and first_check_rcs = #{firstCheckRcs} 
		  		   and first_check_lcs = #{firstCheckLcs} 
		  		   and check_date = #{checkDate} 
		  		   and second_check_vd = #{secondCheckVd} 
		  		   and second_check_rps = #{secondCheckRps} 
		  		   and second_check_lps = #{secondCheckLps} 
		  		   and second_check_rcs = #{secondCheckRcs} 
		  		   and second_check_lcs = #{secondCheckLcs} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and first_check_pd = #{firstCheckPd} 
		  		   and second_check_pd = #{secondCheckPd} 
		  		   and identity_card = #{identityCard} 



                order by check_date desc




			limit #{offset}, #{limit}

