mapper.select:
		select `id`,`student_name`,`student_sex`,`nation`,`birthday`,`identity_card`,`school`,`grade`,`student_class`,`phone`,`address`,`height`,`weight`,`add_time`,`status`,q_r_code,last_check_time
		from y_student

		  		   and id = #{id} 
		  		   and student_name = #{studentName} 
		  		   and student_sex = #{studentSex} 
		  		   and nation = #{nation} 
		  		   and birthday = #{birthday} 
		  		   and identity_card = #{identityCard} 
		  		   or phone = #{identityCard} 
		  		   and school = #{school} 
		  		   and grade = #{grade} 
		  		   and student_class = #{studentClass} 

		  		   and address = #{address} 
		  		   and height = #{height} 
		  		   and weight = #{weight} 
		  		   and add_time = #{addTime} 
		  		   and status = #{status} 
		  		   and q_r_code = #{QRCode} 
		  		   and last_check_time = #{lastCheckTime} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from y_student

  		   and id = #{id} 
  		   and student_name = #{studentName} 
  		   and student_sex = #{studentSex} 
  		   and nation = #{nation} 
  		   and birthday = #{birthday} 
  		   and identity_card = #{identityCard} 
  		   and school = #{school} 
  		   and grade = #{grade} 
  		   and student_class = #{studentClass} 
  		   and phone = #{phone} 
  		   and address = #{address} 
  		   and height = #{height} 
  		   and weight = #{weight} 
  		   and add_time = #{addTime} 
  		   and status = #{status} 
  		   and q_r_code = #{QRCode} 
  		   and last_check_time = #{lastCheckTime}

		select * from y_answer_result

			  and identity_card = #{identityCard} 
			  and DATE_FORMAT(add_time,'%Y-%m-%d') = #{addTime} 



                order by ${sort} ${order}


                order by answer_id desc



			limit #{offset}, #{limit}
